import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
// import { Line } from 'react-chartjs-2';
import PropTypes from 'prop-types';
// import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// // Registering the necessary chart elements for Chart.js version 3+
// ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);


const Chart = ({ priceHistory }) => {
  // Format the data received via props
  // const labels = priceHistory.map(item => item.date); // assuming your priceHistory items have a date
  // const data = priceHistory.map(item => item.price); // assuming priceHistory items have a price value

  if (!priceHistory || priceHistory.length === 0) {
    return <p className="text-red-500">No data available for chart rendering</p>;
  }

  console.log(priceHistory);
  // const labels = priceHistory.map(item => item.date); // Extract dates for X-axis
  // const data = priceHistory.map(item => item.price);


  const data = priceHistory.map(item => ({
    date: item.date,
    price: item.price,
  }));
  // const data = {
  //   labels: priceHistory.map(item => item.date),
  //   series: [priceHistory.map(item => item.price)],
  // };

  // const options = {
  //   high: Math.max(...priceHistory.map(item => item.price)) + 10,
  //   low: Math.min(...priceHistory.map(item => item.price)) - 10,
  // };

  // console.log(labels);
  // console.log(data);

  // const chartData = {
  //   labels: labels,
  //   datasets: [
  //     {
  //       label: 'Stock Price ($)',
  //       data: data,
  //       fill: false,
  //       borderColor: 'rgba(75, 192, 192, 1)',
  //       tension: 0.1,
  //     },
  //   ],
  // };

  // const options = {
  //   responsive: true,
  //   plugins: {
  //     title: {
  //       display: true,
  //       text: 'Stock Price Over Time',
  //     },
  //   },
  // };

  return (
    <div className="mt-6 p-6 bg-gray-800 shadow-lg rounded-xl text-center border border-gray-700">
      <p className="text-lg font-medium text-gray-300">Stock Price Chart</p>
      <div className="h-64 bg-gray-700 rounded-xl">
        {/* <Line data={chartData} options={options} /> */}
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="price" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

Chart.propTypes = {
  priceHistory: PropTypes.array.isRequired,
};

export default Chart;
