import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import PropTypes from 'prop-types';


const Chart = ({ priceHistory }) => {

  if (!priceHistory || priceHistory.length === 0) {
    return <p className="text-red-500">No data available for chart rendering</p>;
  }

  console.log(priceHistory);

  const data = priceHistory.map(item => ({
    date: item.date,
    price: item.price,
  }));

  return (
    <div className="mt-6 p-6 bg-gray-800/50 shadow-lg rounded-xl text-center hover:bg-gray-700 hover:shadow-lg transition duration-300 border border-gray-700">
      <p className="mb-5 text-lg font-medium text-gray-300">Stock Price Chart</p>
      <div className="h-64 bg-gray-700 rounded-tl-lg">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="price" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

Chart.propTypes = {
  priceHistory: PropTypes.array.isRequired,
};

export default Chart;
