import React from 'react';
import { Line } from 'react-chartjs-2';
import PropTypes from 'prop-types';

Chart.propTypes = {
  priceHistory: PropTypes.array.isRequired,
};

const Chart = ({ priceHistory }) => {
  // Format the data received via props
  const labels = priceHistory.map(item => item.date); // assuming your priceHistory items have a date
  const data = priceHistory.map(item => item.price); // assuming priceHistory items have a price value

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Stock Price ($)',
        data: data,
        fill: false,
        borderColor: 'rgba(75, 192, 192, 1)',
        tension: 0.1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Stock Price Over Time',
      },
    },
  };

  return (
    <div className="mt-6 p-6 bg-gray-800 shadow-lg rounded-xl text-center border border-gray-700">
      <p className="text-lg font-medium text-gray-300">Stock Price Chart</p>
      <div className="h-64 bg-gray-700 rounded-xl">
        <Line data={chartData} options={options} />
      </div>
    </div>
  );
};

export default Chart;
