import React, { useState } from 'react';
import axios from 'axios';
import texts from '../texts.json';

const Main = () => {

  const [symbol, setSymbol] = useState('');
  const [trackingSymbol, setTrackingSymbol] = useState('');
  const [stockInfo, setStockInfo] = useState(null);
  const [error, setError] = useState(null);
  const [symbolToTrack, setSymbolToTrack] = useState(null);

  const handleSymbolChange = (e) => {
    setSymbol(e.target.value);
  };

  const handleTrackingSymbolChange = (e) => {
    setTrackingSymbol(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!symbol.trim()) {
      setError('Field can not be empty.');
      return;
    }
    try {
      const response = await axios.get(`http://localhost:3000/stock/${symbol}`);
      setStockInfo(response.data);
      setError(null);
    } catch (error) {
      console.error(`Error fetching stock information: ${error}`);
      if (error.response && error.response.status === 404) {
        setError('Stock symbol not found.');
      } else {
        setError('Error fetching stock information. Please try again.');
      }
    }
  }

  const handleStartTracking = async () => {
    if (!trackingSymbol.trim()) {
      setError('Field can not be empty.');
      return;
    }
    try {
      await axios.put(`http://localhost:3000/stock/${trackingSymbol}`);
      setError(null);
      setSymbolToTrack(trackingSymbol);
    } catch (error) {
      console.error(`Error starting tracking: ${error}`);
      if (error.response && error.response.status === 404) {
        setError('Stock symbol not found.');
      } else {
        setError('Error fetching stock information. Please try again.');
      }
    }
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString();
  };

  return (
    <div className="flex flex-col items-center justify-start bg-gray-900 text-gray-100 px-4">
      <div className="flex-grow">
        <h1 className="text-5xl font-bold text-green-400 mb-6">{texts.stockSymbolGetter}</h1>
      <div className="mt-3 max-w-2xl text-center space-y-2">
        <p className="text-lg font-light text-gray-300">{texts.usageInfo}</p>
        <p className="text-sm font-light text-gray-400">{texts.noteInfo}</p>
      </div>
      <form
        onSubmit={handleFormSubmit}
        className="mt-8 w-full max-w-lg bg-gray-800/50 shadow-lg rounded-2xl p-6 flex flex-col gap-4 border border-gray-700">
        <label>
          <span className="text-lg font-medium text-gray-300">{texts.symbolName}</span>
        </label>
        <input 
          className="w-full p-3 bg-gray-700 text-gray-200 rounded-lg focus:ring-2 focus:ring-green-400 placeholder-gray-400 uppercase" 
          type="text"
          value={symbol}
          onChange={handleSymbolChange} 
          placeholder="AAPL, TSLA, AMZN..."
        />
          <button type="submit" className="w-full py-3 bg-green-500 text-white font-semibold rounded-lg text-lg hover:bg-green-600 transition">
            {texts.check}
          </button>
        <label>
          <span className="text-lg font-medium text-gray-300">{texts.trackingSymbol}</span>
        </label>
        <input
          className="w-full p-3 bg-gray-700 text-gray-200 rounded-lg focus:ring-2 focus:ring-blue-400 placeholder-gray-400 uppercase"
          type="text"
          value={trackingSymbol}
          onChange={handleTrackingSymbolChange}
          placeholder="Enter stock to track"
        />
          <button
            type="button"
            onClick={handleStartTracking}
            className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg text-lg hover:bg-blue-600 transition"
          >
            {texts.startTracking}
          </button>
        {error && <p className="text-red-500 font-medium text-center mt-2">{error}</p>}
      </form>

      {symbolToTrack && (
        <div className="mt-8 px-6 py-3 bg-gray-800 rounded-lg shadow-md border border-gray-700">
          <p className="text-lg font-medium text-gray-300">{texts.currentlyTracking}</p>
          <p className="text-2xl font-bold text-green-400 uppercase">{symbolToTrack}</p>
        </div>
      )}
      {stockInfo && (
        <div className="mt-6 p-6 bg-gray-800 shadow-lg rounded-xl text-center border border-gray-700">
          <p className="text-lg font-medium text-gray-300">{texts.symbol}: <span className="font-bold text-green-400">{stockInfo.symbol}</span></p>
          <p className="text-lg font-medium text-gray-300">{texts.currentPrice}: <span className="font-bold text-blue-400">{stockInfo.currentPrice}$</span></p>
          <p className="text-lg font-medium text-gray-300">{texts.lastUpdatedTime}: <span className="text-gray-400">{formatTimestamp(stockInfo.lastUpdatedTime)}</span></p>
          <p className="text-lg font-medium text-gray-300">{texts.movingAverage}: <span className="text-purple-400 font-bold">{stockInfo.movingAverage.toFixed(3)}$</span></p>
        </div>
      )}
      </div>
    </div>
  )
}

export default Main
