import React from 'react';
import axios from 'axios';

const Main = () => {

  const [symbol, setSymbol] = useState('');
  const [stockInfo, setStockInfo] = useState(null);

  const handleSymbolChange = (e) => {
    setSymbol(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:3000/stock/${symbol}`);
      setStockInfo(response.data);
    } catch (error) {
      console.error('Error fetching stock information:', error);
    }
  }
  return (
    <div>
      <div className="bg-amber-600">
        <h1 className="font-satoshi text-2xl text-slate-300 p-6">Stock symbol getter</h1>
      </div>
      <div className="mt-6">
        <p className="font-satoshi text-2xl text-slate-300">Insert any official NASDAQ stock symbol to find out the current price and the moving average.</p>
        <p className="font-satoshi text-xl text-slate-300">(Note: The moving average can only be calculated after 10 minutes, because the server periodically asks for the current price, and we need 10 pieces of data)</p>
      </div>
      <form
          onSubmit={handleFormSubmit}
          className="mt-8 w-full flex flex-col gap-3 glassmorphism ustify-center items-center">
            <label>
              <span className="font-satoshi text-xl text-slate-300">Symbol name</span>
            </label>
            <input 
                className="p-3 m-3 h-8 rounded-md" 
                type="text" />
            <div className="flex-end mx-3 mb-5 gap-4">
              <button type="submit" className="px-14 py-2 text-xl bg-amber-600 rounded-full text-slate-100">
                Check
              </button>
            </div>
        </form>

      {stockInfo && (
        <div>
          <p>{`Symbol: ${stockInfo.symbol}`}</p>
          <p>{`Current Price: ${stockInfo.currentPrice}`}</p>
          <p>{`Last Updated Time: ${stockInfo.lastUpdatedTime}`}</p>
          <p>{`Moving Average: ${stockInfo.movingAverage}`}</p>
        </div>
      )}
    </div>
  )
}

export default Main
